// App.js
import React, { useState } from 'react';
import styled, { createGlobalStyle } from 'styled-components';

// Import Google Fonts globally
const GlobalStyle = createGlobalStyle`
  @import url("https://fonts.googleapis.com/css2?family=Montserrat:wght@400;600&display=swap");

  *, *::before, *::after {
    box-sizing: border-box;
  }
  body {
    margin: 0;
    font-family: 'Montserrat', sans-serif;
    background: #f0f3f7;
    color: #1a1a2e;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }
  a {
    text-decoration: none;
    color: inherit;
  }
  button {
    font-family: inherit;
  }
`;

// Color palette for reuse
const colors = {
  blueLight: '#667eea',
  blueDark: '#5a67d8',
  purpleLight: '#764ba2',
  purpleDark: '#6b46c1',
  warmAccent: '#ff6b6b',
  warmAccentDark: '#ee5a24',
  shadow: 'rgba(102, 126, 234, 0.3)',
  textPrimary: '#1a1a2e',
  textSecondary: '#666',
  white: '#fff',
  backgroundLight: '#f0f3f7'
};

// ---------- Reusable Button component ----------
const Button = styled.button`
  background: linear-gradient(145deg, ${colors.blueLight}, ${colors.purpleLight});
  color: ${colors.white};
  border: none;
  padding: 0.65rem 1.8rem;
  border-radius: 30px;
  cursor: pointer;
  font-weight: 600;
  font-size: 1rem;
  box-shadow:
    0 4px 8px ${colors.shadow},
    inset 0 -2px 4px rgba(0,0,0,0.2);
  transition: all 0.3s ease;
  user-select: none;

  &:hover, &:focus {
    background: linear-gradient(145deg, ${colors.purpleLight}, ${colors.blueLight});
    box-shadow:
      0 6px 12px ${colors.shadow},
      inset 0 -3px 6px rgba(0,0,0,0.25);
    outline: none;
    transform: translateY(-2px);
  }
`;

// ---------- Header and Navbar ----------

const HeaderContainer = styled.header`
  background: rgba(255,255,255,0.9);
  backdrop-filter: blur(14px);
  box-shadow: 0 6px 20px rgba(0,0,0,0.1);
  position: sticky;
  top: 0;
  z-index: 1100;
`;

const HeaderContent = styled.div`
  max-width: 1180px;
  margin: 0 auto;
  padding: 1rem 2rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const Logo = styled.a`
  font-weight: 700;
  font-size: 1.8rem;
  background: linear-gradient(135deg, ${colors.blueLight} 0%, ${colors.purpleLight} 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  cursor: pointer;
  user-select: none;
  transition: transform 0.3s ease;

  &:hover, &:focus {
    transform: scale(1.1);
    outline: none;
  }
`;

const Nav = styled.nav`
  display: flex;
  gap: 2rem;
  align-items: center;

  @media (max-width: 768px) {
    display: none;
  }
`;

const NavLink = styled.a`
  font-weight: 600;
  color: ${colors.textSecondary};
  font-size: 1rem;
  padding: 0.25rem 0.5rem;
  border-radius: 6px;
  transition: color 0.3s ease, background-color 0.3s ease;

  &:hover, &:focus {
    color: ${colors.blueDark};
    background-color: ${colors.backgroundLight};
    outline: none;
  }
`;

// Hamburger styles for mobile
const Hamburger = styled.button`
  display: none;
  flex-direction: column;
  justify-content: space-around;
  width: 25px;
  height: 22px;
  background: none;
  border: none;
  cursor: pointer;
  padding: 0;
  z-index: 1200;

  &:focus {
    outline: none;
  }

  span {
    width: 100%;
    height: 3px;
    background-color: ${colors.blueLight};
    border-radius: 2px;
    transition: all 0.3s ease;
    transform-origin: 1px;
  }

  @media (max-width: 768px) {
    display: flex;
  }
`;

// Mobile menu popup
const MobileMenu = styled.div`
  position: absolute;
  top: 72px;
  right: 2rem;
  background: ${colors.white};
  padding: 1.5rem 2rem;
  box-shadow: 0 8px 30px rgba(0,0,0,0.12);
  border-radius: 12px;
  display: flex;
  flex-direction: column;
  gap: 1rem;
  z-index: 1150;
`;

const MobileNavLink = styled.a`
  font-weight: 600;
  color: ${colors.textPrimary};
  font-size: 1.1rem;
  &:hover, &:focus {
    color: ${colors.blueDark};
    outline: none;
  }
`;

// ---------- Main content area ----------

const MainContainer = styled.main`
  max-width: 1000px;
  margin: 3rem auto;
  padding: 2rem;
  background: ${colors.white};
  box-shadow: 0 10px 30px rgba(0,0,0,0.07);
  border-radius: 15px;
  min-height: 60vh;

  @media (max-width: 768px) {
    margin: 1.5rem;
    padding: 1.5rem;
  }
`;

// ---------- Card component example for content ----------

const Card = styled.section`
  background: rgba(230, 236, 255, 0.7);
  padding: 1.5rem 2rem;
  border-radius: 15px;
  box-shadow: 
    4px 4px 14px rgba(102, 126, 234, 0.35),
    -4px -4px 14px rgba(255, 255, 255, 0.7);
  margin-bottom: 2rem;
  transition: transform 0.3s ease;

  &:hover, &:focus-within {
    transform: translateY(-5px);
    box-shadow:
      6px 6px 20px rgba(102, 126, 234, 0.45),
      -6px -6px 20px rgba(255, 255, 255, 0.85);
  }
`;

const CardTitle = styled.h2`
  margin-top: 0;
  margin-bottom: 0.75rem;
  color: ${colors.purpleDark};
`;

const CardContent = styled.p`
  margin: 0;
  color: ${colors.textSecondary};
  line-height: 1.6;
`;

// ---------- App component ----------

function App() {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => setMenuOpen(open => !open);

  return (
    <>
      <GlobalStyle />
      
      <HeaderContainer role="banner">
        <HeaderContent>
          <Logo href="#" tabIndex="0" aria-label="Home page">
            üåê Web Scraper
          </Logo>

          <Nav role="navigation" aria-label="Primary Navigation">
            <NavLink href="#" tabIndex="0">Home</NavLink>
            <NavLink href="#" tabIndex="0">About</NavLink>
            <NavLink href="#" tabIndex="0">Contact</NavLink>
            <Button tabIndex="0">Get Started</Button>
          </Nav>

          <Hamburger
            aria-label="Toggle navigation menu"
            aria-expanded={menuOpen}
            aria-controls="mobile-menu"
            onClick={toggleMenu}
          >
            <span />
            <span />
            <span />
          </Hamburger>

          {menuOpen && (
            <MobileMenu id="mobile-menu" role="menu">
              <MobileNavLink href="#" role="menuitem" tabIndex="0">Home</MobileNavLink>
              <MobileNavLink href="#" role="menuitem" tabIndex="0">About</MobileNavLink>
              <MobileNavLink href="#" role="menuitem" tabIndex="0">Contact</MobileNavLink>
              <Button tabIndex="0" onClick={() => alert('Get Started clicked!')}>
                Get Started
              </Button>
            </MobileMenu>
          )}
        </HeaderContent>
      </HeaderContainer>

      <MainContainer role="main" tabIndex={-1}>
        <Card tabIndex="0">
          <CardTitle>Welcome to the Web Scraper Application</CardTitle>
          <CardContent>
            This app helps you scrape web content quickly and efficiently with a modern and intuitive interface.
            Explore features and get started now!
          </CardContent>
        </Card>

        <Card tabIndex="0">
          <CardTitle>Responsive & Accessible</CardTitle>
          <CardContent>
            The design adapts seamlessly across devices, providing smooth user experience and accessibility for all users.
          </CardContent>
        </Card>
      </MainContainer>
    </>
  );
}

export default App;
