import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import styled from 'styled-components';
import { GoogleLogin } from '@react-oauth/google';
import jwtDecode from 'jwt-decode';   // fixed import syntax
import Header from './components/Header';
// import Dashboard from './components/Dashboard';
import Login from './components/Login';
import LoadingSpinner from './components/LoadingSpinner';

// Styled components for the main app layout
const AppContainer = styled.div`
  min-height: 100vh;
  display: flex;
  flex-direction: column;
`;

const MainContent = styled.main`
  flex: 1;
  padding: 20px;
  max-width: 1200px;
  margin: 0 auto;
  width: 100%;
`;

// Temporary Placeholder component in place of Dashboard
const PlaceholderDashboard = ({ user }) => (
  <div>
    <h2>Welcome, {user?.name || 'User'}!</h2>
    <p>This is a placeholder dashboard. Please implement your Dashboard component.</p>
  </div>
);

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  // Check for existing user session on app load
  useEffect(() => {
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
    setLoading(false);
  }, []);

  // Handle successful Google login
  const handleLoginSuccess = (credentialResponse) => {
    try {
      const decoded = jwtDecode(credentialResponse.credential); // works with v4
      const userInfo = {
        id: decoded.sub,
        name: decoded.name,
        email: decoded.email,
        picture: decoded.picture,
        loginTime: new Date().toISOString()
      };
      setUser(userInfo);
      localStorage.setItem('user', JSON.stringify(userInfo));
    } catch (error) {
      console.error('Error decoding JWT:', error);
    }
  };

  // Handle login error
  const handleLoginError = () => {
    console.error('Login failed');
  };

  // Handle logout
  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('user');
  };

  // Protected route component
  const ProtectedRoute = ({ children }) => {
    return user ? children : <Navigate to="/login" replace />;
  };

  if (loading) {
    return <LoadingSpinner />;
  }

  return (
    <AppContainer>
      <Header user={user} onLogout={handleLogout} />
      <MainContent>
        <Routes>
          <Route 
            path="/" 
            element={
              <ProtectedRoute>
                <PlaceholderDashboard user={user} />
              </ProtectedRoute>
            } 
          />
          <Route 
            path="/login" 
            element={
              user ? (
                <Navigate to="/" replace />
              ) : (
                <Login
                  onLoginSuccess={handleLoginSuccess}
                  onLoginError={handleLoginError}
                />
              )
            } 
          />
          <Route path="*" element={<Navigate to="/" replace />} />
        </Routes>
      </MainContent>
    </AppContainer>
  );
}

export default App;
